import React, { useState } from 'react'
import NavBar from '../components/NavBar'
import { Button, Container, Form } from 'react-bootstrap'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'


const Signup = () => {
    const [data , setData ] = useState({
        username:"",
        email:"",
        password:"" 
    })
    const navigate = useNavigate();

    const handleChange = (e)=>{
        setData(prev=>({...prev, [e.target.name]: [e.target.value]}))
        console.log(setData)
    }
    const handleSubmit = async (e)=>{
        e.preventDefault();
        try{
            const res = axios.post('http://localhost:3000/register/signup', data)
            navigate('/')

        }catch(err){
            console.log(err)
        }
    }

  return (
    <>
        <NavBar />
        <Container style={{textAlign:'center',justifyContent:'center'}}>
            <h1 className='m-auto'>
                    Register
            </h1>
            <Form className='m-auto' style={{justifyContent:'center'}}>
                <Form.Group className='col-3 m-auto '>
                    <Form.Control className='m-3' onClick={handleChange}  name='username' type='text' placeholder='Username'></Form.Control>
                    <Form.Control className='m-3' onClick={handleChange} name='email' type='email' placeholder='Email'></Form.Control>
                    <Form.Control className='m-3' onClick={handleChange} name='password' type='password' placeholder='password'></Form.Control>
                </Form.Group>
                    <Form.Group className='col-4 m-auto'>
                        <Button className='col-4 m-3' href='#' onClick={handleSubmit}  >Sign up</Button>
                    </Form.Group>
            </Form>
        </Container>
    </>

  )
}

export default Signup
